package application;

public class Reunion1 {
	private int idrèunion;
	private String date;
	private int idencadrant;
	private int idgroup;
	private String student1;
	private String student2;
	public Reunion1() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Reunion1(String date, int idencadrant, int idgroup, String student1, String student2) {
		super();
		this.date = date;
		this.idencadrant = idencadrant;
		this.idgroup = idgroup;
		this.student1 = student1;
		this.student2 = student2;
	}
	public int getIdrèunion() {
		return idrèunion;
	}
	public void setIdrèunion(int idrèunion) {
		this.idrèunion = idrèunion;
	}
	public String getDate() {
		return date;
	}
	public void setDate(String date) {
		this.date = date;
	}
	public int getIdencadrant() {
		return idencadrant;
	}
	public void setIdencadrant(int idencadrant) {
		this.idencadrant = idencadrant;
	}
	public int getIdgroup() {
		return idgroup;
	}
	public void setIdgroup(int idgroup) {
		this.idgroup = idgroup;
	}
	public String getStudent1() {
		return student1;
	}
	public void setStudent1(String student1) {
		this.student1 = student1;
	}
	public String getStudent2() {
		return student2;
	}
	public void setStudent2(String student2) {
		this.student2 = student2;
	}
	
	
	
	
	

}
	
	
	